mapper.select:

where 1=1 

	and  t.year_Id=#{yearId}


	and  t.term_Id=#{termId}


	and  a.grade_Id=#{gradeId}


	and  t.class_Id=#{classId}


	and  t.TEACHER_ID=#{teacherId}


	and  t.SUBJECT_ID=#{subjectId}


		select t.teacher_id as teacherId ,t.subject_id as subjectId,t.name as subjectName, t.week_arrange as weekArrange from( 
			select t.TEACHER_ID,t.SUBJECT_ID,a.name,t.WEEK_ARRANGE from t_teacher_subject t
			left join  t_subject a on t.subject_id = a.subject_id
			where 1=1

		 	and  t.year_Id=#{yearId}


		 	and  t.term_Id=#{termId}


		 	and  t.CLASS_ID in  

     		 #{item}  


			group by t.TEACHER_ID,t.SUBJECT_ID,a.name,t.WEEK_ARRANGE  having count(1) <4) t
			where t.week_arrange >1 and t.week_arrange <6 and t.week_arrange is not null

select t.TEACHER_ID as teacherId,t.SUBJECT_ID as subjectId,t.countNum as count ,t.totalWeekArrange as totalWeekArrange,a.`NAME` as teacherName,b.`NAME` as subjectName from (SELECT t.TEACHER_ID, t.SUBJECT_ID, count(1) as countNum, SUM(WEEK_ARRANGE) as totalWeekArrange FROM t_teacher_subject t WHERE 1=1
and t.year_Id=#{yearId}
and t.term_Id=#{termId}
and t.CLASS_ID in (select class_id from t_school_class where grade_id = #{gradeId})
GROUP BY t.TEACHER_ID, t.SUBJECT_ID ORDER BY sum(WEEK_ARRANGE) desc ,count(1) desc) t left join t_teacher a on t.TEACHER_ID = a.TEACHER_ID left join t_subject b on t.SUBJECT_ID = b.SUBJECT_ID


where 1=1
	and  t.year_Id=#{0}
	and  t.term_Id=#{1}
	and  a.grade_Id=#{2}
order by t.CLASS_ID,t.SUBJECT_ID
